$(document).ready(function () {
    $('#dataPhoto').DataTable({
        processing: true,
        serverSide: true,
        ajax: '/dataPhoto',
        columns: [
            { data: 'id', name: 'id', class: 'lead-text' },
            { data: 'name', name: 'name', class: 'text-muted text-capitalize' },
            { data: 'photo', name: 'photo', class: 'text-center' },
            { data: 'location', name: 'location', class: 'text-muted' },
            { data: 'description', name: 'description', class: 'text-muted' },
            { data: 'action', name: 'action' }
        ]
    });

    $('#dataVideo').DataTable({
        processing: true,
        serverSide: true,
        ajax: '/dataVideo',
        columns: [
            { data: 'id', name: 'id', class: 'lead-text' },
            { data: 'name', name: 'name', class: 'text-muted text-capitalize' },
            { data: 'video', name: 'video', class: 'text-center' },
            { data: 'location', name: 'location', class: 'text-muted' },
            { data: 'description', name: 'description', class: 'text-muted' },
            { data: 'action', name: 'action' }
        ]
    });
    
    $('#dataEvent').DataTable({
        processing: true,
        serverSide: true,
        ajax: '/dataEvent',
        columns: [
            { data: 'id', name: 'id', class: 'lead-text' },
            { data: 'name', name: 'name', class: 'text-muted text-capitalize' },
            { data: 'phone', name: 'phone', class: 'text-muted text-center' },
            { data: 'event', name: 'event', class: 'text-muted text-center text-capitalize' },
            { data: 'date_start', name: 'date_start', class: 'text-muted text-center' },
            { data: 'date_done', name: 'date_done', class: 'text-muted text-center' },
            { data: 'location', name: 'location', class: 'text-muted' },
            { data: 'status', name: 'status', class: 'text-center' },
            { data: 'action', name: 'action' }
        ]
    });
})