$(document).ready(function () {
    $('#dataPhoto').DataTable({
        processing: true,
        serverSide: true,
        ajax: '/dataPhoto',
        columns: [
            { data: 'id', name: 'id', class: 'lead-text' },
            { data: 'name', name: 'name', class: 'text-muted text-capitalize' },
            { data: 'photo', name: 'photo', class: 'text-center' },
            { data: 'location', name: 'location', class: 'text-muted text-center' },
            { data: 'description', name: 'description', class: 'text-muted' },
            { data: 'action', name: 'action' }
        ]
    });
})