$(document).ready(function () {
    $('#dataPhoto').DataTable({
        processing: true,
        serverSide: true,
        ajax: '/dataPhoto',
        columns: [
            { data: 'id', name: 'id', class: 'lead-text' },
            { data: 'product_name', name: 'product_name', class: 'text-muted text-capitalize' },
            { data: 'price', name: 'price', class: 'text-muted' },
            { data: 'action', name: 'action' }
        ]
    });
})